import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Loginn from "./Login";
import UserDashboard from "./pages/UserDashboard";
import AdminDashboard from "./pages/AdminDashboard";

function App() {
  const [user, setUser] = React.useState(null);
  const [mode, setMode] = React.useState(null); // "user" or "admin"

  const handleLogin = (userData, role) => {
    setUser(userData);
    setMode(role);
  };

  const handleLogout = () => {
    setUser(null);
    setMode(null);
  };

  return (
    <Router>
      <Routes>
        {!user ? (
          <Route path="/*" element={<Login onLogin={handleLogin} />} />
        ) : mode === "user" ? (
          <Route path="/*" element={<UserDashboard user={user} onLogout={handleLogout} />} />
        ) : (
          <Route path="/*" element={<AdminDashboard user={user} onLogout={handleLogout} />} />
        )}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
