import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  useNavigate,
} from "react-router-dom";
import Loginn from "./Loginn";
import App from "./App";

function Root() {
  const [user, setUser] = useState(null); // Full user object

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={<LoginWrapper onLogin={(user) => setUser(user)} />}
        />

        <Route
          path="/admin"
          element={
            user?.role === "admin" ? (
              <App mode="admin" user={user} onLogout={() => setUser(null)} />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        <Route
          path="/user"
          element={
            user?.role === "user" ? (
              <App mode="user" user={user} onLogout={() => setUser(null)} />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}

function LoginWrapper({ onLogin }) {
  const navigate = useNavigate();

  const handleLogin = (user) => {
    onLogin(user); // Pass full user object
    if (user.role === "admin") {
      navigate("/admin", { replace: true });
    } else if (user.role === "user") {
      navigate("/user", { replace: true });
    }
  };

  return <Loginn onLogin={handleLogin} />;
}
