import React, { useState } from "react";
import { Routes, Route, Navigate, useNavigate } from "react-router-dom";
import Loginn from "./Loginn";
import App from "./App";

function Root() {
  const [user, setUser] = useState(null);

  const handleLogin = (user) => {
    setUser(user);
  };

  return (
    <Routes>
      <Route
        path="/login"
        element={<Loginn onLogin={handleLogin} />}
      />

      <Route
        path="/admin"
        element={
          user?.role === "admin" ? (
            <App mode="admin" user={user} onLogout={() => setUser(null)} />
          ) : (
            <Navigate to="/login" replace />
          )
        }
      />

      <Route
        path="/user"
        element={
          user?.role === "user" ? (
            <App mode="user" user={user} onLogout={() => setUser(null)} />
          ) : (
            <Navigate to="/login" replace />
          )
        }
      />

      <Route path="*" element={<Navigate to="/login" replace />} />
    </Routes>
  );
}

export default Root;
